#!/bin/bash# SLURM job settings#SBATCH --job-name=toy_job            # Job name#SBATCH --output=toy_job_%j.out       # Output file name (%j expands to jobID)#SBATCH --error=toy_job_%j.err        # Error file name (%j expands to jobID)#SBATCH --ntasks=1                    # Number of tasks (processes)#SBATCH --time=00:05:00               # Maximum runtime (5 minutes)#SBATCH --partition=debug             # Partition to submit to#SBATCH --mem=100M                    # Memory limit per node (100 MB)# Print the current date and timeecho"Job started on $(date)"# Run your command hereecho"Hello, SLURM!"# Print the date and time againecho"Job finished on $(date)"
